import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../css/searchstyle.css";
import "react-datepicker/dist/react-datepicker.css";
/* Import Components */

import Form from "react-bootstrap/Form";
import Badge from "react-bootstrap/Badge";
import DatePicker from "react-datepicker";
import Dropdown from "react-bootstrap/Dropdown";
import DropdownButton from "react-bootstrap/DropdownButton";
import Button from "react-bootstrap/Button";
import axios from "axios";

class BuyForm extends Component {
  state = {
    name: "",
    email: "",
    contact: "",
    startDate: new Date(),
    typeOfService: "",
    photo: "",
    photoName: ""
  };

  handleSubmit = () => {};

  onPictureChange = picture => {
    this.setState({ photo: picture });
  };

  onDateChange = date => {
    this.setState({ startDate: date });
  };

  onNameChange = newName => {
    this.setState({ name: newName });
  };

  onEmailChange = newEmail => {
    this.setState({ email: newEmail });
  };

  onContactChange = newContact => {
    this.setState({ contact: newContact });
  };

  onServiceChange = newService => {
    this.setState({ typeOfService: newService });
  };

  handleSubmit = async event => {
    event.preventDefault();
    const res = await axios.post(
      "http://localhost:8080/apis/post/buy",
      this.state,
      {
        headers: { "Content-Type": "application/json" }
      }
    );
    console.log()
    if (res.status === 200) console.log("Successful");
    else console.log("Error");
  };

  render() {
    return (
      <div>
        <div class="container">
          <header>
            <h2>
              <a href="/">
                <i class="ion-plane"></i> BLINK EXCHANGE
              </a>
            </h2>
            <nav>
              <ul>
                <li>
                  <a href="/sell" title="Sell">
                    Sell
                  </a>
                </li>

                <li>
                  <a href="/" title="Return to HOME Page">
                    Return to Home Page
                  </a>
                </li>
                <li>
                  <a href="/" title="LOG OUT">
                    LOG OUT
                  </a>
                </li>
              </ul>
            </nav>
          </header>
        </div>
        <Form>
          <Form.Row>
            <Form.Group controlId="itemName">
              <h4>
                <Badge variant="danger">Product/Service Name</Badge>
              </h4>
              <Form.Control
                type="text"
                placeholder="Enter Product/Service Name"
                onChange={this.onNameChange}
              />
            </Form.Group>
            <Form.Group controlId="email">
              <h4>
                <Badge variant="danger">Email</Badge>
              </h4>
              <Form.Control
                type="email"
                placeholder="Enter your contact email"
                onChange={this.onEmailChange}
              />
            </Form.Group>
          </Form.Row>
          <Form.Group controlId="dateOfService">
            <h4>
              <Badge variant="danger">Date Of Service</Badge>
            </h4>
            <DatePicker
              selected={this.state.startDate}
              onChange={this.onDateChange}
            />
          </Form.Group>
          <Form.Group controlId="phoneNumber">
            <h4>
              <Badge variant="danger">Phone Number</Badge>
            </h4>
            <Form.Control
              onChange={this.onContactChange}
              type="phone"
              placeholder="Enter your phone number"
            />
          </Form.Group>
          <Form.Row>
            <Form.Group controlId="serviceType">
              <DropdownButton
                id="chooseService"
                title="Choose the Product/Service Type"
                onChange={this.onServiceChange}
                selected={this.typeOfService}
              >
                <Dropdown.Item>Service</Dropdown.Item>
                <Dropdown.Item>Electronics</Dropdown.Item>
                <Dropdown.Item>Books</Dropdown.Item>
                <Dropdown.Item>Clothes</Dropdown.Item>
                <Dropdown.Item>Other</Dropdown.Item>
              </DropdownButton>
            </Form.Group>
            <Form.Group controlId="chooseImage">
              <h4>
                <Badge variant="danger" placeholder={this.state.photoName}>
                  Upload Image
                  <input
                    type="file"
                    accept=".jpg.png.jpeg"
                    onChange={this.onPictureChange}
                  />
                </Badge>
              </h4>
            </Form.Group>
          </Form.Row>
          <Form.Row>
            <Form.Group controlId="buttons">
              <Button
                variant="primary"
                type="submit"
                onSubmit={this.handleSubmit}
              >
                Submit
              </Button>
            </Form.Group>
            <Form.Group>
              <Button
                variant="secondary"
                type="reset"
                onClick={this.handleClearForm}
                href="/"
              >
                Reset
              </Button>
            </Form.Group>
          </Form.Row>
        </Form>
      </div>
    );
  }
}

export default BuyForm;
