import React, { Component } from "react";

/* Import Components */
import CheckBox from "../components/CheckBox";
import Input from "../components/Input";
import TextArea from "../components/TextArea";
import Select from "../components/Select";
import Button from "../components/Button";
import "../css/emptyCSS.css";

class BuyForm extends Component {
  constructor(props) {
    super(props);

    this.state = {
      newBuyItem: {
        name: "",
        date: "",
        typeofService: "",
        skills: [],
        email: ""
      },

      typeofServiceOptions: [
        "Service",
        "Electronics",
        "Books",
        "Clothes",
        "Other"
      ],
      skillOptions: ["Programming", "Development", "Design", "Testing"]
    };
    this.handleTextArea = this.handleTextArea.bind(this);
    this.handledate = this.handledate.bind(this);
    this.handleFullName = this.handleFullName.bind(this);
    this.handleFormSubmit = this.handleFormSubmit.bind(this);
    this.handleClearForm = this.handleClearForm.bind(this);
    this.handleCheckBox = this.handleCheckBox.bind(this);
    this.handleInput = this.handleInput.bind(this);
  }

  /* This lifecycle hook gets executed when the component mounts */

  handleFullName(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newBuyItem: {
          ...prevState.newBuyItem,
          name: value
        }
      }),
      () => console.log(this.state.newBuyItem)
    );
  }

  handledate(e) {
    let value = e.target.value;
    this.setState(
      prevState => ({
        newBuyItem: {
          ...prevState.newBuyItem,
          date: value
        }
      }),
      () => console.log(this.state.newBuyItem)
    );
  }

  handleInput(e) {
    let value = e.target.value;
    let name = e.target.name;
    this.setState(
      prevState => ({
        newBuyItem: {
          ...prevState.newBuyItem,
          [name]: value
        }
      }),
      () => console.log(this.state.newBuyItem)
    );
  }

  handleTextArea(e) {
    console.log("Inside handleTextArea");
    let value = e.target.value;
    this.setState(
      prevState => ({
        newBuyItem: {
          ...prevState.newBuyItem,
          email: value
        }
      }),
      () => console.log(this.state.newBuyItem)
    );
  }

  handleCheckBox(e) {
    const newSelection = e.target.value;
    let newSelectionArray;

    if (this.state.newBuyItem.skills.indexOf(newSelection) > -1) {
      newSelectionArray = this.state.newBuyItem.skills.filter(
        s => s !== newSelection
      );
    } else {
      newSelectionArray = [...this.state.newBuyItem.skills, newSelection];
    }

    this.setState(prevState => ({
      newBuyItem: { ...prevState.newBuyItem, skills: newSelectionArray }
    }));
  }

  handleFormSubmit(e) {
    e.preventDefault();
    let userData = this.state.newBuyItem;

    fetch("172.16.38.62:8080/apis/post/buy", {
      method: "POST",
      body: JSON.stringify(userData),
      headers: {
        Accept: "application/json",
        "Content-typeofService": "application/json"
      }
    }).then(response => {
      response.json().then(response => {
        console.log("Successful");
      });
    });
  }

  handleClearForm(e) {
    e.preventDefault();
    this.setState({
      newBuyItem: {
        name: "",
        date: "",
        typeofService: "",
        skills: [],
        email: ""
      }
    });
  }

  render() {
    return (
      <div className="">
        <form className="container-fluid" onSubmit={this.handleFormSubmit}>
          <Input
            inputtypeofService={"text"}
            title={"Full Name"}
            name={"name"}
            value={this.state.newBuyItem.name}
            placeholder={"Enter your name"}
            handleChange={this.handleInput}
          />{" "}
          {/* Name of the user */}
          <Input
            inputtypeofService={"number"}
            name={"date"}
            title={"date"}
            value={this.state.newBuyItem.date}
            placeholder={"Enter your date"}
            handleChange={this.handledate}
          />{" "}
          {/* date */}
          <Select
            title={"typeofService"}
            name={"typeofService"}
            options={this.state.typeofServiceOptions}
            value={this.state.newBuyItem.typeofService}
            placeholder={"Select typeofService"}
            handleChange={this.handleInput}
          />{" "}
          {/* date Selection */}
          <CheckBox
            title={"Skills"}
            name={"skills"}
            options={this.state.skillOptions}
            selectedOptions={this.state.newBuyItem.skills}
            handleChange={this.handleCheckBox}
          />{" "}
          {/* Skill */}
          <TextArea
            title={"email you."}
            rows={10}
            value={this.state.newBuyItem.email}
            name={"currentPetInfo"}
            handleChange={this.handleTextArea}
            placeholder={"Describe your past experience and skills"}
          />
          {/* email you */}
          <Button
            action={this.handleFormSubmit}
            typeofService={"primary"}
            title={"Submit"}
            style={buttonStyle}
          />{" "}
          {/*Submit */}
          <Button
            action={this.handleClearForm}
            typeofService={"secondary"}
            title={"Clear"}
            style={buttonStyle}
          />{" "}
          {/* Clear the form */}
        </form>
      </div>
    );
  }
}

const buttonStyle = {
  margin: "10px 10px 10px 10px"
};

export default BuyForm;
